-- the following take a string for the name to return an index for the relevent function

-- types
function Attacks_GetTypeIndexByName(typename)
    typename = string.lower(typename)
    if typename == "normal" then
        return 0
    elseif typename == "fighting" then
        return 1
    elseif typename == "flying" then
        return 2
    elseif typename == "poison" then
        return 3
    elseif typename == "ground" then
        return 4
    elseif typename == "rock" then
        return 5
    elseif typename == "bug" then
        return 6
    elseif typename == "ghost" then
        return 7
    elseif typename == "steel" then
        return 8
    elseif typename == "fire" then
        return 9
    elseif typename == "water" then
        return 10
    elseif typename == "grass" then
        return 11
    elseif typename == "electric" then
        return 12
    elseif typename == "psychic" then
        return 13
    elseif typename == "ice" then
        return 14
    elseif typename == "dragon" then
        return 15
    elseif typename == "dark" then
        return 16
    elseif typename == "blank" then
        return 17
    else
        return 17
    end
end

--category
function Attacks_GetCategoryIndexByName(typename)
    typename = string.lower(typename)
    if typename == "physical" then
        return 0
    elseif typename == "special" then
        return 1
    elseif typename == "status" then
        return 2
    else
        return 0
    end
end

--attack category, doesn't match any pokemon stats, it's for internal engine use. 
-- damage and special damage always do damage, i forget if there is any reason to use one over the other. damage is default
-- special forgoes trying to use the attack as a damaging move and skips to the special attack delegate
function Attacks_GetAttackCategoryIndexByName(typename)
    typename = string.lower(typename)
    if typename == "damage" then
        return 0
    elseif typename == "special" then
        return 1
    elseif typename == "specialdamage" then
        return 2
    else
        return 0
    end
end

--contest category
function Attacks_GetContestCategoryIndexByName(typename)
    typename = string.lower(typename)
    if typename == "tough" then
        return 0
    elseif typename == "smart" then
        return 1
    elseif typename == "beauty" then
        return 2
    elseif typename == "cool" then
        return 3
    elseif typename == "cute" then
        return 4
    else
        return 0
    end
end

-- takes a type index and gives you a nice display name for messages
