    elseif typeidx == 9 then
        return "Fire"
    elseif typeidx == 10 then
        return "Water"
    elseif typeidx == 11 then
        return "Grass"
    elseif typeidx == 12 then
        return "Electric"
    elseif typeidx == 13 then
        return "Psychic"
    elseif typeidx == 14 then
        return "Ice"
