    end
end

-- this function simplifies using the target string to get your actual target if you are doing something to a target other than the primary one
function Attacks_GetTargetStringByAttackTarget(desiredtarget)
    if desiredtarget == "user" then
        if Attacks_TargetString == "0" then
            return "1"
        elseif Attacks_TargetString == "1" then
            return "0"
        end
    elseif desiredtarget == "target" then
        return Attacks_TargetString
    end
end

-- the following takes a number between 0 and 100 and compares it to a random roll to determine if the effect should trigger
-- also a string, current valid values are "user" and "target". "user" will work out the caster's target string by inverting the current one

-- do a percentage chance check and if true put the target to sleep
function Attacks_TryToSleep(chance, target)
    local rollthebones = math.random(0, 100)
    local T = ""
    if rollthebones <= chance then
        if string.lower(target) == "target" then
            T = Attacks_TargetString
        elseif string.lower(target) == "user" then
            if Attacks_TargetString == "1" then
                T = "0"
            elseif Attacks_TargetString == "0" then
                T = "1"
            end
        end
        Attacks_AddBattleStep("SpecialStatus", T .. "|asleep")
        return true
    end
    return false
end

